member.summary:
             .

             .

             Usado para iniciar um menu

             Por em formLoad


             Como usar:

             
            ModMenu.Menu.CreateMenu(this, "novo menu", () =>
            {
               ----Funcoes
               ModMenu.Menu.EndMenu();
            });

             .

             .

             Funcao usada para finalizar um menu

             Usar dentro da funcao lambda do CreateMenu


             Exemplo:

             
            ModMenu.Menu.CreateMenu(this, "novo menu", () =>
            {
               ----Funcoes
               ModMenu.Menu.EndMenu();

            });

             .

             .

             Funcao que adiciona uma opcao simples que executa clicando
             .
.

             Como usar:

             
            ModMenu.Menu.CreateMenu(this, "novo menu", () =>
            {
               ModMenu.Menu.addOption("Opcao", () =>
                 {
                     --Executa ao clicar
                 });
               ModMenu.Menu.EndMenu();
            });

             .

             .

             Funcao que adiciona uma opcao para criar um submenu
             .
.

             Como usar:

             
            ModMenu.Menu.CreateMenu(this, "Menu", () =>
            {
               ModMenu.Menu.addMenuOption("Novo menu", () =>
                 {
                     --ModMenu.Menu.CreateMenu(this,.....
                 });
               ModMenu.Menu.EndMenu();
            });

             .

             .

             Funcao que adiciona uma opcao para alterar INT
             .
.

             Como usar: Variavel deve ser passada como REF

             


            public static int gg = 0;
            
            ModMenu.Menu.CreateMenu(this, "Menu", () =>
            {
                ModMenu.Menu.addInt("1", ref gg, 0, 1000, (x) =>
                               {
                                   //Aqui o codigo roda em loop, cuidado
                                   gg = x;
                                });
               ModMenu.Menu.EndMenu();
            });

             .

             .

             Funcao que adiciona uma opcao para alterar ARRAY
             .
.

             Como usar: Variavel deve ser passada como REF

             
            public static string[] nomes = { "du", "dudu", "edu" };
            public static int gg = 0;
             .

            ModMenu.Menu.CreateMenu(this, "Menu", () =>
            {
                ModMenu.Menu.addArray("array", ref nomes, ref gg, (x) =>
                               {
                                   //Aqui o codigo roda em loop, cuidado
                                   gg = x;
                                });
               ModMenu.Menu.EndMenu();
            });

             .

             .

             Funcao que adiciona uma opcao para alterar FLOAT
             .
.

             Como usar: Variavel deve ser passada como REF

             


            public static double vvvv = 0.0;
            
            ModMenu.Menu.CreateMenu(this, "Menu", () =>
            {
                ModMenu.Menu.addFloat("float", ref vvvv, -10.0, 10.0, 0.40, (x) =>
                               {
                                   //Aqui o codigo roda em loop, cuidado
                                   vvvv = x;
                                });
               ModMenu.Menu.EndMenu();
            });

             .

